muclient.script:
-------------------------------------------------- -- Invmon --------------------------------------------------
Invmon = {}
Invmon.ACTION = { ["REMOVED"] = 1, ["WORN"] = 2, ["LOST"] = 3, ["RECEIVED"] = 4, ["CONTAINER_GET"] = 5, ["CONTAINER_PUT"] = 6, ["CONSUMED"] = 7, ["VAULT_PUT"] = 9, ["VAULT_GET"] = 10, ["KEYRING_PUT"] = 11, ["KEYRING_GET"] = 12, }
Invmon.ITEM_FLAG = { ["KEPT"] = "K", ["INVIS"] = "I", ["MAGICAL"] = "M", ["GLOWING"] = "G", ["HUMMING"] = "H", ["CURSED"] = "C", ["ENVENOMED"] = "E", ["TEMPERED"] = "T", ["WEAKENED"] = "W", }
Invmon.ITEM_TYPE = { ["LIGHT"] = 1, ["SCROLL"] = 2, ["WAND"] = 3, ["STAFF"] = 4, ["WEAPON"] = 5, ["TREASURE"] = 6, ["ARMOR"] = 7, ["POTION"] = 8, ["FURNITURE"] = 9, ["TRASH"] = 10, ["CONTAINER"] = 11, ["DRINK"] = 12, ["KEY"] = 13, ["FOOD"] = 14, ["BOAT"] = 15, ["CORPSE_MOB"] = 16, ["CORPSE_PLAYER"] = 17, ["FOUNTAIN"] = 18, ["PILL"] = 19, ["PORTAL"] = 20, ["BEACON"] = 21, ["GIFT_CARD"] = 22, ["RAW_MATERIAL"] = 24, ["CAMP_FIRE"] = 25, ["FORGE"] = 26, ["RUNESTONE"] = 27, }
function Invmon.item_received(trigger, line, wc) if (wc.item:match("^%d+ %*")) then -- Multiple items received. Ignore. return end if (wc.item ~= wc.name) then -- Likely another case of multiple items being received, where -- the final invmon tag refers to an item lower down in the -- stack. return end -- Strip pretitle if any from owner name. wc.owner = wc.owner:match("(%a+)$") local item = Item:new(wc) Envenom.item_received(item) end
function Invmon.item_given(trigger, line, wc) if (wc.item:match("^%d+ %*")) then -- Multiple items given. Ignore. return end Envenom.item_given(wc.id) end
function Invmon.gold_received(trigger, line, wc) local amount = wc.amount:gsub(",", "") Envenom.gold_received(amount) end
function Invmon.enable(on) -- Enable/disable invmon tags via GMCP. Default behavior is to -- enable. To disable them, `on` must be false. if (IsConnected()) then local setting = on == false and "no" or "yes" Send_GMCP_Packet("config invmon " .. setting) end end
function Invmon.monitor(on) -- Set monitoring of receipt and giveing of single items by the -- Invmon module. When `on` is false, no invmon data will be -- processed. EnableTriggerGroup("group_invmon", on) end

-------------------------------------------------- -- GMCP --------------------------------------------------
GMCP = {}
GMCP.STATE = { ["LOGIN"] = 1, ["MOTD"] = 2, ["ACTIVE"] = 3, ["AFK"] = 4, ["NOTE"] = 5, ["EDIT"] = 6, ["PAGER"] = 7, ["FIGHTING"] = 8, ["SLEEPING"] = 9, ["SITTING"] = 11, ["RUNNING"] = 12, }
function GMCP.get_state() return tonumber(gmcp("char.status.state")) or -1 end
function GMCP.get_location() local uid = gmcp("room.info.num") or "-1" local room = gmcp("room.info.name") or "Somewhere" local zone = Utility.pascal(gmcp("room.info.zone")) or "Somewhere" local msg = "@G%s @w/ %s @w(#@Y%d@w)" return msg:format(zone, room, uid) end
function GMCP.get_subclass() return gmcp("char.base.subclass") or "" end
