muclient.script:
--------------------------------------------------
-- Config
--------------------------------------------------

Config = {}


function Config.initialize()
    Config.load()
end


function Config.default()
    local data = {
    }
    return json.encode(data)
end


function Config.load()
    Config.data = json.decode(var.config or Config.default())
end


function Config.save()
    var.config = json.encode(Config.data)
end


function Config.get(key)
    return Config.data[key]
end


function Config.set(key, val)
    Config.data[key] = val
    Config.save()
end

-------------------------------------------------- -- GMCP --------------------------------------------------
GMCP = {}
function GMCP.enable_invmon() if (IsConnected()) then Send_GMCP_Packet("config invmon yes") end end
